mapper.select:
SELECT /* selectQnaDtl */ a.titl , a.cntn , a.atch_file_id , a.qstn_seq , a.ancm_yn , a.reg_id FROM co_qna_mst a
a.qstn_seq = #{detailsKey}
and (a.reg_id = #{regId} OR a.ancm_yn = 'Y')

mapper.insert:
	INSERT /* insertQnaDtl */ INTO co_qna_mst
  		(
	  		 qstn_seq
			,titl
			,cntn
			,atch_file_id
			,ancm_yn
			,reg_id
			,reg_dtm
			,mod_id
			,mod_dtm
		)
	VALUES
		(
		  #{qstnSeq}
		, #{titl}
		, #{cntn}
		, #{atchFileId}
		, #{ancmYn}
		, #{regId}
		, NOW()
		, #{modId}
		, NOW()
		)

mapper.update:
		UPDATE /* updateQnaDtl */ co_qna_mst
		SET  titl = #{titl}
		   , cntn = #{cntn}
		   , atch_file_id = #{atchFileId}
		   , ancm_yn = #{ancmYn}
		   , mod_id = #{modId}
		   , mod_dtm = NOW()
		WHERE qstn_seq = #{detailsKey}

    		and reg_id = #{regId}

mapper.select:
SELECT /* getQnaCommentList */ B.qstn_seq , B.rply_seq , B.titl , B.reg_id , (SELECT NAME FROM co_adm_mst WHERE ID = B.reg_id LIMIT 1) as REG_NM , B.reg_dtm , B.REG_ID FROM co_qna_mst A JOIN co_qna_comment_dtl B ON A.qstn_seq = B.qstn_seq WHERE B.qstn_seq = #{detailsKey}
and A.reg_id = #{regId}
ORDER BY B.REG_DTM ASC

mapper.insert:
	INSERT /* insertQnaComment */ INTO co_qna_comment_dtl
  		(
	  		 qstn_seq
	  		,rply_seq
			,titl
			,reg_id
			,reg_dtm
			,mod_id
			,mod_dtm
		)
	VALUES
		(
		  #{detailsKey}
		, #{rplySeq}
		, #{titl}
		, #{regId}
		, NOW()
		, #{regId}
		, NOW()
		)

mapper.delete:
		DELETE FROM /* deleteQnaComment */ co_qna_comment_dtl
		WHERE qstn_seq = #{detailsKey} AND rply_seq = #{rplySeq}

    		and reg_id = #{regId}

		DELETE FROM /* deleteQna */ co_qna_mst
		WHERE qstn_seq = #{detailsKey}

    		and reg_id = #{regId}
